sys_script_client.script:
function onLoad() {

	if(g_form.getValue('state')==1 && 'software' == g_form.getValue('category')){
		var setShortDesc = 'Description of incident is set for Incident in new status';
		if (setShortDesc!=g_form.getValue('short_description') ){
			g_form.clearValue();
			g_form.setValue('short_description', setShortDesc);
			g_form.setValue('description', setShortDesc);
			alert("Short description and Description value set To - " + g_form.getValue('short_description'));
		}	
	}	
}
