function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var ga = new GlideAjax("sendingEmail42");
    ga.addParam('sysparm_name', 'gettingDepValues');
    ga.addParam('sysparm_userName', newValue);
    ga.getXML(gettingValues);

    function gettingValues(response) {
		alert("response si - "+ response);
        var answer = response.responseXML.documentElement.getAttribute("answer");
        alert("answer : " +answer);
		var value = answer.split(',');
		g_form.setValue('x_58872_needit_calleremail', value[0]);
		g_form.setValue('x_58872_needit_department', value[1]);
    }
    //Type appropriate comment here, and begin script below

}
