sys_script_include.script:
var sendingEmail42 = Class.create();
sendingEmail42.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    gettingEmail: function() {
        var selectedValue = this.getParameter("sysparm_userName");
        var ga = new GlideRecord("sys_user");
        ga.addQuery("sys_id", selectedValue);
        ga.query();
        if (ga.next()) {
            return ga.email;
        }
    },
    gettingDepValues: function() {
        var selectedValue = this.getParameter("sysparm_userName");
        var arr = [];
        var user = new GlideRecord("sys_user");
        user.addQuery("sys_id", selectedValue);
        user.query();
        if (user.next()) {
            arr.push(user.email.toString());
            arr.push(user.department.toString());
        }
        return arr.toString();
    },

    getChatGPTResponse: function(restService, restMethod, requestor) {

        gs.info("Inside Script inclde fuction fro chatgpt response");
        var r = new sn_ws.RESTMessageV2(restService, restMethod);
        r.setStringParameterNoEscape('temp', '0.7');
        r.setStringParameterNoEscape('Question', requestor);
        r.setStringParameterNoEscape('model', 'gpt-3.5-turbo');

        var response = r.execute();
        var responseBody = response.getBody();
        var httpStatus = response.getStatusCode();

        var responseObj = JSON.parse(responseBody);
		gs.info("chat gpt response - " + responseObj.choices[0].message.content);
        return responseObj.choices[0].message.content;

    },
    sendingCondition: function(requestor) {
        // 		var ga = new GlideRecord("sys_user");
        // 		ga.addQuery("company", requestor.company);
        // 		ga.query();
        // 		var arr = [];
        // 		while(ga.next()){
        // 			arr.push(ga.sys_id.toString());
        // 		}
        // 		return "sys_idIN"+arr;
        return "company=" + requestor.company;
    },

    type: 'sendingEmail42'
});
